function technotes_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('technotes');
	if(!$arrPerm['insert']) return false;

	$data = [
		'technote_tech' => Request::lookup('technote_tech', ''),
		'technote_note' => Request::val('technote_note', ''),
		'technote_author' => parseCode('<%%creatorUsername%%>', true),
		'technote_timestamp' => parseCode('<%%creationDateTime%%>', true),
	];

	if($data['technote_tech'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Technote tech': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['technote_note'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Technote note': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: technotes_before_insert
	if(function_exists('technotes_before_insert')) {
		$args = [];
		if(!technotes_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('technotes', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('technotes', $recID, calculated_fields()['technotes']);

	// hook: technotes_after_insert
	if(function_exists('technotes_after_insert')) {
		$res = sql("SELECT * FROM `technotes` WHERE `technote_ID`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!technotes_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('technotes', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) technotes_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function technotes_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function technotes_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('technotes', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: technotes_before_delete
	if(function_exists('technotes_before_delete')) {
		$args = [];
		if(!technotes_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `technotes` WHERE `technote_ID`='{$selected_id}'", $eo);

	// hook: technotes_after_delete
	if(function_exists('technotes_after_delete')) {
		$args = [];
		technotes_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='technotes' AND `pkValue`='{$selected_id}'", $eo);
}

function technotes_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('technotes', $selected_id, 'edit')) return false;

	$data = [
		'technote_tech' => Request::lookup('technote_tech', ''),
		'technote_note' => Request::val('technote_note', ''),
		'technote_editor' => parseCode('<%%editorUsername%%>', false),
		'technote_editorts' => parseCode('<%%editingDateTime%%>', false),
	];

	if($data['technote_tech'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Technote tech': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['technote_note'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Technote note': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('technotes', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: technotes_before_update
	if(function_exists('technotes_before_update')) {
		$args = ['old_data' => $old_data];
		if(!technotes_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'technotes', 
		backtick_keys_once($set), 
		['`technote_ID`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="technotes_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('technotes', $data['selectedID'], calculated_fields()['technotes']);

	// hook: technotes_after_update
	if(function_exists('technotes_after_update')) {
		$res = sql("SELECT * FROM `technotes` WHERE `technote_ID`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['technote_ID'];
		$args = ['old_data' => $old_data];
		if(!technotes_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='technotes' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

