			$templateCode = str_replace('<%%HTMLAREA(technote_note)%%>', '<textarea name="technote_note" id="technote_note" rows="5">' . safe_html(htmlspecialchars_decode($row['technote_note'])) . '</textarea>', $templateCode);
		} else {
			$templateCode = str_replace('<%%HTMLAREA(technote_note)%%>', '<div id="technote_note" class="form-control-static">' . $row['technote_note'] . '</div>', $templateCode);
		}
		$templateCode = str_replace('<%%VALUE(technote_note)%%>', nl2br($row['technote_note']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(technote_note)%%>', urlencode($urow['technote_note']), $templateCode);
		$templateCode = str_replace('<%%VALUE(technote_author)%%>', safe_html($urow['technote_author']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(technote_author)%%>', urlencode($urow['technote_author']), $templateCode);
		$templateCode = str_replace('<%%VALUE(technote_timestamp)%%>', safe_html($urow['technote_timestamp']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(technote_timestamp)%%>', urlencode($urow['technote_timestamp']), $templateCode);
		$templateCode = str_replace('<%%VALUE(technote_editor)%%>', safe_html($urow['technote_editor']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(technote_editor)%%>', urlencode($urow['technote_editor']), $templateCode);
		$templateCode = str_replace('<%%VALUE(technote_editorts)%%>', safe_html($urow['technote_editorts']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(technote_editorts)%%>', urlencode($urow['technote_editorts']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(technote_ID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(technote_ID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(technote_tech)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(technote_tech)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(technote_note)%%>', '<textarea name="technote_note" id="technote_note" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%VALUE(technote_author)%%>', '<%%creatorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(technote_author)%%>', urlencode('<%%creatorUsername%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(technote_timestamp)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(technote_timestamp)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(technote_editor)%%>', '<%%editorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(technote_editor)%%>', urlencode('<%%editorUsername%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(technote_editorts)%%>', '<%%editingDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(technote_editorts)%%>', urlencode('<%%editingDateTime%%>'), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('technotes');
	if($selected_id) {
		$jdata = get_joined_record('technotes', $selected_id);
		if($jdata === false) $jdata = get_defaults('technotes');
		$rdata = $row;
	}
	$templateCode .= loadView('technotes-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: technotes_dv
	if(function_exists('technotes_dv')) {
		$args = [];
		technotes_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}