function supportcase_notes_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('supportcase_notes');
	if(!$arrPerm['insert']) return false;

	$data = [
		'sc_notedatetime' => parseCode('<%%creationDateTime%%>', true),
		'sc_noteauthor' => Request::val('sc_noteauthor', ''),
		'sc_notecase' => Request::lookup('sc_notecase', ''),
		'sc_notedetails' => br2nl(Request::val('sc_notedetails', '')),
	];


	// hook: supportcase_notes_before_insert
	if(function_exists('supportcase_notes_before_insert')) {
		$args = [];
		if(!supportcase_notes_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('supportcase_notes', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('supportcase_notes', $recID, calculated_fields()['supportcase_notes']);

	// hook: supportcase_notes_after_insert
	if(function_exists('supportcase_notes_after_insert')) {
		$res = sql("SELECT * FROM `supportcase_notes` WHERE `sc_noteID`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!supportcase_notes_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('supportcase_notes', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) supportcase_notes_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

