function supportcase_notes_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('supportcase_notes', $selected_id, 'edit')) return false;

	$data = [
		'sc_noteauthor' => Request::val('sc_noteauthor', ''),
		'sc_notecase' => Request::lookup('sc_notecase', ''),
		'sc_notedetails' => br2nl(Request::val('sc_notedetails', '')),
	];

	// get existing values
	$old_data = getRecord('supportcase_notes', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: supportcase_notes_before_update
	if(function_exists('supportcase_notes_before_update')) {
		$args = ['old_data' => $old_data];
		if(!supportcase_notes_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'supportcase_notes', 
		backtick_keys_once($set), 
		['`sc_noteID`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="supportcase_notes_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('supportcase_notes', $data['selectedID'], calculated_fields()['supportcase_notes']);

	// hook: supportcase_notes_after_update
	if(function_exists('supportcase_notes_after_update')) {
		$res = sql("SELECT * FROM `supportcase_notes` WHERE `sc_noteID`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['sc_noteID'];
		$args = ['old_data' => $old_data];
		if(!supportcase_notes_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='supportcase_notes' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

