			$templateCode = str_replace('<%%VALUE(sc_notedetails)%%>', safe_html($urow['sc_notedetails']), $templateCode);
		} else {
			$templateCode = str_replace('<%%VALUE(sc_notedetails)%%>', safe_html($urow['sc_notedetails'], true), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(sc_notedetails)%%>', urlencode($urow['sc_notedetails']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(sc_noteID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(sc_noteID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(sc_notedatetime)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(sc_notedatetime)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(sc_noteauthor)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(sc_noteauthor)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(sc_notecase)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(sc_notecase)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(sc_notedetails)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(sc_notedetails)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('supportcase_notes');
	if($selected_id) {
		$jdata = get_joined_record('supportcase_notes', $selected_id);
		if($jdata === false) $jdata = get_defaults('supportcase_notes');
		$rdata = $row;
	}
	$templateCode .= loadView('supportcase_notes-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: supportcase_notes_dv
	if(function_exists('supportcase_notes_dv')) {
		$args = [];
		supportcase_notes_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}