function tblwopubstatus_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('tblwopubstatus', $selected_id, 'edit')) return false;

	$data = [
		'wopub_WO' => Request::lookup('wopub_WO', ''),
		'wopub_status' => Request::val('wopub_status', ''),
		'wopub_Comments' => br2nl(Request::val('wopub_Comments', '')),
	];

	// get existing values
	$old_data = getRecord('tblwopubstatus', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: tblwopubstatus_before_update
	if(function_exists('tblwopubstatus_before_update')) {
		$args = ['old_data' => $old_data];
		if(!tblwopubstatus_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'tblwopubstatus', 
		backtick_keys_once($set), 
		['`wopub_ID`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="tblwopubstatus_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('tblwopubstatus', $data['selectedID'], calculated_fields()['tblwopubstatus']);

	// hook: tblwopubstatus_after_update
	if(function_exists('tblwopubstatus_after_update')) {
		$res = sql("SELECT * FROM `tblwopubstatus` WHERE `wopub_ID`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['wopub_ID'];
		$args = ['old_data' => $old_data];
		if(!tblwopubstatus_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='tblwopubstatus' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

