	$templateCode = str_replace('<%%UPLOADFILE(wopub_ID)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(wopub_WO)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(wopub_Updated)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(wopub_status)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(wopub_Comments)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		$templateCode = str_replace('<%%VALUE(wopub_ID)%%>', safe_html($urow['wopub_ID']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(wopub_ID)%%>', urlencode($urow['wopub_ID']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(wopub_WO)%%>', safe_html($urow['wopub_WO']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(wopub_WO)%%>', html_attr($row['wopub_WO']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(wopub_WO)%%>', urlencode($urow['wopub_WO']), $templateCode);
		$templateCode = str_replace('<%%VALUE(wopub_Updated)%%>', safe_html($urow['wopub_Updated']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(wopub_Updated)%%>', urlencode($urow['wopub_Updated']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(wopub_status)%%>', safe_html($urow['wopub_status']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(wopub_status)%%>', html_attr($row['wopub_status']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(wopub_status)%%>', urlencode($urow['wopub_status']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(wopub_Comments)%%>', safe_html($urow['wopub_Comments']), $templateCode);
		} else {
			$templateCode = str_replace('<%%VALUE(wopub_Comments)%%>', safe_html($urow['wopub_Comments'], true), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(wopub_Comments)%%>', urlencode($urow['wopub_Comments']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(wopub_ID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(wopub_ID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(wopub_WO)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(wopub_WO)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(wopub_Updated)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(wopub_Updated)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(wopub_status)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(wopub_status)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(wopub_Comments)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(wopub_Comments)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('tblwopubstatus');
	if($selected_id) {
		$jdata = get_joined_record('tblwopubstatus', $selected_id);
		if($jdata === false) $jdata = get_defaults('tblwopubstatus');
		$rdata = $row;
	}
	$templateCode .= loadView('tblwopubstatus-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: tblwopubstatus_dv
	if(function_exists('tblwopubstatus_dv')) {
		$args = [];
		tblwopubstatus_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}