function workorders_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('workorders');
	if(!$arrPerm['insert']) return false;

	$data = [
		'wo_createdby' => parseCode('<%%creatorUsername%%>', true),
		'wo_datecreated' => parseCode('<%%creationDateTime%%>', true),
		'wo_Status' => Request::val('wo_Status', ''),
		'wo_assignedto' => Request::lookup('wo_assignedto', ''),
		'wo_client' => Request::lookup('wo_client', ''),
		'wo_ticket' => Request::val('wo_ticket', ''),
		'wo_asset' => Request::lookup('wo_asset', ''),
		'wo_Title' => Request::val('wo_Title', ''),
		'wo_Description' => Request::val('wo_Description', ''),
	];

	if($data['wo_Status'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Status': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['wo_client'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Client': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['wo_Title'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Title': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}

	// hook: workorders_before_insert
	if(function_exists('workorders_before_insert')) {
		$args = [];
		if(!workorders_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('workorders', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('workorders', $recID, calculated_fields()['workorders']);

	// hook: workorders_after_insert
	if(function_exists('workorders_after_insert')) {
		$res = sql("SELECT * FROM `workorders` WHERE `wo_ID`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!workorders_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('workorders', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) workorders_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function workorders_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// copy workordernotes
	$res = sql("SELECT * FROM `workordernotes` WHERE `wonote_wo`='{$safe_sid}'", $eo);
	while($row = db_fetch_assoc($res)) {
		$data = [
			'SelectedID' => $row['wonote_ID'],
			'wonote_wo' => $destination_id,
			'wonote_note' => $row['wonote_note'],
		];

		$ch = curl_insert_handler('workordernotes', $data);
		if($ch !== false) $requests[] = $ch;
	}

	// launch requests, asynchronously
	curl_batch($requests);
}

