function workorders_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('workorders', $selected_id, 'edit')) return false;

	$data = [
		'wo_Status' => Request::val('wo_Status', ''),
		'wo_assignedto' => Request::lookup('wo_assignedto', ''),
		'wo_client' => Request::lookup('wo_client', ''),
		'wo_ticket' => Request::val('wo_ticket', ''),
		'wo_asset' => Request::lookup('wo_asset', ''),
		'wo_Title' => Request::val('wo_Title', ''),
		'wo_Description' => Request::val('wo_Description', ''),
	];

	if($data['wo_Status'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Status': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['wo_client'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Client': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['wo_Title'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Title': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('workorders', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: workorders_before_update
	if(function_exists('workorders_before_update')) {
		$args = ['old_data' => $old_data];
		if(!workorders_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'workorders', 
		backtick_keys_once($set), 
		['`wo_ID`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="workorders_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('workorders', $data['selectedID'], calculated_fields()['workorders']);

	// hook: workorders_after_update
	if(function_exists('workorders_after_update')) {
		$res = sql("SELECT * FROM `workorders` WHERE `wo_ID`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['wo_ID'];
		$args = ['old_data' => $old_data];
		if(!workorders_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='workorders' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

